CREATE OR REPLACE FUNCTION public.handle_new_ready_user() RETURNS trigger AS $$ BEGIN INSERT INTO public.ready_profiles (id, display_name) VALUES (new.id, COALESCE(new.raw_user_meta_data->>'firstName', '') || ' ' || COALESCE(new.raw_user_meta_data->>'lastName', '')); RETURN new; END; $$ LANGUAGE plpgsql SECURITY DEFINER; DROP TRIGGER IF EXISTS on_auth_user_created ON auth.users; CREATE TRIGGER on_auth_user_created AFTER INSERT ON auth.users FOR EACH ROW EXECUTE FUNCTION public.handle_new_ready_user();
